const body = $json.body || $json || {};
const orderId = String(body.orderId || '').trim();
const txHash = String(body.txHash || '').trim();
const order = body.order || {};

if (!orderId) return [{ json: { ok: false, error: 'order_id_required' } }];
if (!/^0x[a-fA-F0-9]{64}$/.test(txHash)) return [{ json: { ok: false, error: 'invalid_tx_hash' } }];

const buyerAddress = String(order.buyerAddress || '').toLowerCase();
const merchantAddress = String(order.merchantAddress || '').toLowerCase();
const usdcAmountMicro = String(order.usdcAmountMicro || '');
const expiresAt = String(order.expiresAt || '');

if (!/^0x[a-fA-F0-9]{40}$/.test(buyerAddress)) return [{ json: { ok: false, error: 'invalid_order_buyer' } }];
if (!/^0x[a-fA-F0-9]{40}$/.test(merchantAddress)) return [{ json: { ok: false, error: 'invalid_order_merchant' } }];
if (!/^\d+$/.test(usdcAmountMicro)) return [{ json: { ok: false, error: 'invalid_order_amount' } }];
if (!expiresAt || new Date(expiresAt).getTime() < Date.now()) return [{ json: { ok: false, error: 'order_expired' } }];

const rpcUrl = 'https://mainnet.base.org';
const usdc = '0x833589fcd6edb6e08f4c7c32d4f71b54bda02913';

const rpc = async (method, params) => {
  const res = await fetch(rpcUrl, {
    method: 'POST',
    headers: { 'content-type': 'application/json' },
    body: JSON.stringify({ jsonrpc: '2.0', id: 1, method, params })
  });
  return res.json();
};

const [txResp, rcResp] = await Promise.all([
  rpc('eth_getTransactionByHash', [txHash]),
  rpc('eth_getTransactionReceipt', [txHash])
]);

const tx = txResp?.result;
const receipt = rcResp?.result;
if (!tx || !receipt) return [{ json: { ok: false, error: 'tx_not_found_or_pending' } }];
if (String(receipt.status).toLowerCase() !== '0x1') return [{ json: { ok: false, error: 'tx_failed' } }];

if (String(tx.from || '').toLowerCase() !== buyerAddress) return [{ json: { ok: false, error: 'tx_from_mismatch' } }];
if (String(tx.to || '').toLowerCase() !== usdc) return [{ json: { ok: false, error: 'tx_to_not_usdc_contract' } }];

const parseTransferInput = (data='') => {
  if (!data.startsWith('0xa9059cbb')) return null;
  const hex = data.slice(10);
  if (hex.length < 128) return null;
  const toHex = hex.slice(0, 64);
  const amtHex = hex.slice(64, 128);
  const to = `0x${toHex.slice(24)}`.toLowerCase();
  const amount = BigInt(`0x${amtHex}`).toString();
  return { to, amount };
};

const parsed = parseTransferInput(String(tx.input || ''));
if (!parsed) return [{ json: { ok: false, error: 'not_erc20_transfer' } }];
if (parsed.to !== merchantAddress) return [{ json: { ok: false, error: 'merchant_mismatch' } }];
if (parsed.amount !== usdcAmountMicro) return [{ json: { ok: false, error: 'amount_mismatch' } }];

return [{ json: { ok: true, order: { id: orderId, buyerAddress, merchantAddress, usdcAmountMicro, txHash, confirmedAt: new Date().toISOString() } } }];
