const body = $json || {};
const buyerAddress = String(body.buyerAddress || '').trim().toLowerCase();
const agentId = String(body.agentId || '').trim();

const isAddress = (v='') => /^0x[a-fA-F0-9]{40}$/.test(v);
if (!isAddress(buyerAddress)) {
  return [{ json: { ok: false, error: 'invalid_buyer_address' } }];
}

const merchantAddress = String($env.MERCHANT_ADDRESS || '').trim().toLowerCase();
if (!isAddress(merchantAddress)) {
  return [{ json: { ok: false, error: 'merchant_not_configured' } }];
}

const agentMap = { a1: 120, a2: 180, a3: 240 };
const vPrice = agentMap[agentId];
if (!vPrice) {
  return [{ json: { ok: false, error: 'invalid_agent' } }];
}

const usdcMicroPerVirtual = BigInt($env.USDC_MICRO_PER_VIRTUAL || '50000');
const amountMicro = BigInt(vPrice) * usdcMicroPerVirtual;

const ttlSec = Number($env.ORDER_TTL_SECONDS || '900');
const now = Date.now();
const orderId = `ord_${now}_${Math.random().toString(36).slice(2,8)}`;
const expiresAt = new Date(now + ttlSec * 1000).toISOString();

const store = $getWorkflowStaticData('global');
if (!store.orders) store.orders = {};
store.orders[orderId] = {
  id: orderId,
  agentId,
  buyerAddress,
  merchantAddress,
  usdcAmountMicro: amountMicro.toString(),
  status: 'pending',
  txHash: null,
  createdAt: new Date(now).toISOString(),
  expiresAt,
  confirmedAt: null
};

return [{
  json: {
    ok: true,
    orderId,
    agentId,
    buyerAddress,
    merchantAddress,
    usdcAmountMicro: amountMicro.toString(),
    usdcAmount: Number(amountMicro) / 1_000_000,
    expiresAt
  }
}];
